import React, { Component } from 'react'
import Popup from "reactjs-popup";

class NewPost extends Component{


    constructor(props){
        super(props);
       // this.handleSubmit =this.handleSubmit.bind(this);
        this.state ={
            error:null,
            isLoaded:false,
            profileFetched:false,
            posts:[],
            user:[]
        };
    }
        
    fetchProfile(){
        /*fetch('http://localhost:3000/profile')
        .then(resp=>resp.json())
        .then(
            (result)=>{
                this.setState({
                    profileFetched:true,
                    user:result.name
                });
            }
        )
        .catch(error=>this.setState({error, profileFetched:false}));*/
    }


    componentDidMount(){
       /* fetch('http://localhost:3000/post')
        .then(res=>res.json())
        .then(
            (result)=>{
                this.setState({
                   isLoaded:true,
                   posts:result
                });
            },
            (error)=>{
                this.setState({
                    isLoaded:true,
                    error
                });
            }
        )*/
        fetch('http://localhost:3000/profile')
        .then(resp=>resp.json())
        .then(
            (result)=>{
                this.setState({
                    profileFetched:true,
                    user:result
                });
            }
        )
        .catch(error=>this.setState({error, profileFetched:false}));
    }
/*
    handleSubmit(event){
        event.preventDefault();

        fetch('',{
            methods:'POST',

        })
    }
*/
    render() {
        return (
            <div className="newPoster">

               <Popup trigger={<button className="newPostButton"> Create New Post </button>}
      modal
      closeOnDocumentClick>
            <div>
            <p>Create New Post</p>
            <p>{this.user.name}</p>
            <form onSubmit = {this.handleSubmit}>
              <textarea name="message" rows="10" cols="100" placeholder="Write your status..."></textarea>
              <br/>
              <input type="submit"/>
              </form>
            </div>
            </Popup>
            </div>
        );
    }
}

export default NewPost
