import React, { Component } from 'react'
import Popup from "reactjs-popup";
import Axios from 'axios';

class NewPost extends Component{


    constructor(props){
        super(props)
        this.state ={
            error:null,
          //  isLoaded:false,
            profileFetched:false,
            postContent:'',
            user:'',
            postTime:''
        };
        this.changeHandler = this.changeHandler.bind(this);
    }
        


    componentDidMount(){
       /* fetch('http://localhost:3000/post')
        .then(res=>res.json())
        .then(
            (result)=>{
                this.setState({
                   isLoaded:true,
                   posts:result
                });
            },
            (error)=>{
                this.setState({
                    isLoaded:true,
                    error
                });
            }
        )*/
        fetch('http://localhost:3000/profile')
        .then(resp=>resp.json())
        .then(
            (result)=>{
                this.setState({
                    profileFetched:true,
                    user:result
                });
            },
            (error)=>{this.setState({
                error, 
                profileFetched:false
            });
        }
        )

    }

    handleSubmit(event){
        event.preventDefault();
        /*
        fetch('http://localhost:3000/post',{
            methods:'POST',

        })*/
        Axios
        .post('http://localhost:3000/post', 
        {
            userName: 'this.props.userName',
            postContent:'this.props.postContent',
            postTime:this.props.postTime
        }
        )
        .then(res=>{
            console.log(res)
        })
        .catch(error=>{
            console.log(error)
        })
    }

    changeHandler(event){
        this.setState({
            postContent:event.value
        }
        );
    }

    render() {
        const userName = this.state.user.name;
        return (
            <div className="newPoster">

               <Popup trigger={<button className="newPostButton"> Create New Post </button>}
      modal
      closeOnDocumentClick>
            <div>
            <p>Create New Post</p>
            <p>{this.state.user.name}</p>
            <form onSubmit = {this.handleSubmit}>
              <textarea 
              name="postContent" 
              value={this.postContent} 
              onChange = {this.changeHandler}
              rows="10" cols="100" placeholder="Write your status..."></textarea>
              <br/>
              <input type="submit"/>
              </form>
            </div>
            </Popup>
            </div>
        );
    }
}

export default NewPost
