import React, { Component } from 'react'
import Popup from "reactjs-popup";
import Axios from 'axios';


const axios = require('axios');
class NewPost extends Component{

    
    constructor(props){
        super(props)
        this.state ={
          //  error:null,
          //  isLoaded:false,
          //  profileFetched:false,
            postContent:'',
            UserName:'',
            postTime:''
        };
        this.changeHandler = this.changeHandler.bind(this);
    }
        


    componentDidMount(){
        fetch('http://localhost:3000/profile')
        .then(resp=>resp.json())
        .then(
            (result)=>{
                this.setState({
                    UserName:result.name
                });
            },
            (error)=>{this.setState({
                error, 
                //profileFetched:false
            });
        }
        )

    }

    handleSubmit(event){
        event.preventDefault();
        axios
        .post('http://localhost:3000/post', 
        {
            UserName:NewPost.state.UserName
        }
        )
        .then(res=>{
            console.log(res)
        })
        .catch(error=>{
            console.log(error)
        })
    }

    changeHandler(event){
        this.setState({
            postContent :event.value,
            user: event.name,
            postTime:'0000'
        }
        );
    }

    render() {
        const {postContent, UserName, postTime} = this.state
        return (
            <div className="newPoster">

               <Popup trigger={<button className="newPostButton"> Create New Post </button>}
      modal
      closeOnDocumentClick>
            <div>
            <p>Create New Post</p>
            <p>{UserName}</p>
            <form onSubmit = {this.handleSubmit}>
              <textarea 
              name= {this.state.user}
              value={postContent} 
              onChange = {this.changeHandler}
              rows="10" cols="100" placeholder="Write your status..."></textarea>
              <br/>
              <input type="submit"/>
              </form>
            </div>
            </Popup>
            </div>
        );
    }
}

export default NewPost
